p align=\"left\">&nbsp; // visitor.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void GetSource(CefRefPtr&lt;CefStringVisitor&gt; visitor) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Retrieve this frame\'s display text as a string sent to the specified</p>\n<p align=\"left\">&nbsp; // visitor.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void GetText(CefRefPtr&lt;CefStringVisitor&gt; visitor) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Load the request represented by the |request| object.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void LoadRequest(CefRefPtr&lt;CefRequest&gt; request) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Load the specified |url|.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void LoadURL(const CefString&amp; url) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Load the contents of |string_val| with the specified dummy |url|. |url|</p>\n<p align=\"left\">&nbsp;&nbsp;// should have a standard scheme (for example, http scheme) or behaviors like</p>\n<p align=\"left\">&nbsp; // link clicks and web security restrictions may not behave as expected.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void LoadString(const CefString&amp; string_val,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;const CefString&amp; url) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Execute a string of JavaScript code in this frame. The |script_url|</p>\n<p align=\"left\">&nbsp; // parameter is the URL where the script in question can be found, if any.</p>\n<p align=\"left\">&nbsp; // The renderer may request this URL to show the developer the source of the</p>\n<p align=\"left\">&nbsp; // error.&nbsp; The |start_line| parameter is the base line number to use for error</p>\n<p align=\"left\">&nbsp; // reporting.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=script_url)--*/</p>\n<p align=\"left\">&nbsp; virtual void ExecuteJavaScript(const CefString&amp; code,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; script_url,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int start_line) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Returns true if this is the main (top-level) frame.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool IsMain() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Returns true if this is the focused frame.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool IsFocused() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Returns the name for this frame. If the frame has an assigned name (for</p>\n<p align=\"left\">&nbsp; // example, set via the iframe \"name\" attribute) then that value will be</p>\n<p align=\"left\">&nbsp; // returned. Otherwise a unique name will be constructed based on the frame</p>\n<p align=\"left\">&nbsp; // parent hierarchy. The main (top-level) frame will always have an empty name</p>\n<p align=\"left\">&nbsp; // value.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefString GetName() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Returns the globally unique identifier for this frame or &lt; 0 if the</p>\n<p align=\"left\">&nbsp; // underlying frame does not yet exist.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual int64 GetIdentifier() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Returns the parent of this frame or NULL if this is the main (top-level)</p>\n<p align=\"left\">&nbsp; // frame.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefFrame&gt; GetParent() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Returns the URL currently loaded in this frame.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefString GetURL() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Returns the browser that this frame belongs to.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefBrowser&gt; GetBrowser() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Get the V8 context associated with the frame. This method can only be</p>\n<p align=\"left\">&nbsp; // called from the render process.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefV8Context&gt; GetV8Context() = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Visit the DOM document. This method can only be called from the render</p>\n<p align=\"left\">&nbsp; // process.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void VisitDOM(CefRefPtr&lt;CefDOMVisitor&gt; visitor) = 0;</p>\n<p align=\"left\">};</p>\n<p>&nbsp;</p>\n<h2>1.9&nbsp; CefApp应用程序类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefApp是应用程序类，网页嵌入程序都要实现这样一个应用程序类。提供了一些简单的接口：命令行参数修改，主题修改，获取进程句柄等。还有很多的子类，例如SimpleApp、ClientApp等，clientapp又有很多子类ClientAppBrowser、ClientAppRenderer、ClientAppOther，表示不同的应用类型：应用程序类定义如下：</p>\n<p align=\"left\">class CefApp : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p>&nbsp; ///对命令行参数修改</p>\n<p align=\"left\">&nbsp; // Provides an opportunity to view and/or modify command-line arguments before</p>\n<p align=\"left\">&nbsp; // processing by CEF and Chromium. The |process_type| value will be empty for</p>\n<p align=\"left\">&nbsp; // the browser process. Do not keep a reference to the CefCommandLine object</p>\n<p align=\"left\">&nbsp; // passed to this method. The CefSettings.command_line_args_disabled value</p>\n<p align=\"left\">&nbsp; // can be used to start with an empty command-line object. Any values</p>\n<p align=\"left\">&nbsp; // specified in CefSettings that equate to command-line arguments will be set</p>\n<p align=\"left\">&nbsp; // before this method is called. Be cautious when using this method to modify</p>\n<p align=\"left\">&nbsp; // command-line arguments for non-browser processes as this may result in</p>\n<p align=\"left\">&nbsp; // undefined behavior including crashes.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=process_type)--*/</p>\n<p align=\"left\">&nbsp; virtual void OnBeforeCommandLineProcessing(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; process_type,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefCommandLine&gt; command_line) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Provides an opportunity to register custom schemes. Do not keep a reference</p>\n<p align=\"left\">&nbsp; // to the |registrar| object. This method is called on the main thread for</p>\n<p align=\"left\">&nbsp; // each process and the registered schemes should be the same across all</p>\n<p align=\"left\">&nbsp; // processes.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/注册自定义主题</p>\n<p align=\"left\">&nbsp; virtual void OnRegisterCustomSchemes(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRawPtr&lt;CefSchemeRegistrar&gt; registrar) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for resource bundle events. If</p>\n<p align=\"left\">&nbsp; // CefSettings.pack_loading_disabled is true a handler must be returned. If no</p>\n<p align=\"left\">&nbsp; // handler is returned resources will be loaded from pack files. This method</p>\n<p align=\"left\">&nbsp; // is called by the browser and render processes on multiple threads.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefResourceBundleHandler&gt; GetResourceBundleHandler() {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return NULL;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;///</p>\n<p align=\"left\">&nbsp; // Return the handler for functionality specific to the browser process. This</p>\n<p align=\"left\">&nbsp; // method is called on multiple threads in the browser process.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefBrowserProcessHandler&gt; GetBrowserProcessHandler() {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return NULL;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for functionality specific to the render process. This</p>\n<p align=\"left\">&nbsp; // method is called on the render process main thread.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefRenderProcessHandler&gt; GetRenderProcessHandler() {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return NULL;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">};</p>\n<h2>1.10&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CEF引用计数<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CEF创建对象形式如CefRefPtr&lt;SimpleApp&gt; app(new SimpleApp);或者CefRefPtr&lt;SimpleApp&gt; app = new SimpleApp();创建的指针引用计数由CefRefPtr管理，CefRefPtr通过调用AddRef()和Release()方法自动管理引用计数。CefRefPtr定义如下：</p>\n<p>using CefRefPtr = scoped_refptr&lt;T&gt;;</p>\n<p align=\"left\">template &lt;class T&gt;</p>\n<p align=\"left\">class scoped_refptr {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; typedef T element_type;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; scoped_refptr() : ptr_(NULL) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; scoped_refptr(T* p) : ptr_(p) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; if (ptr_)</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ptr_-&gt;AddRef();</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; scoped_refptr(const scoped_refptr&lt;T&gt;&amp; r) : ptr_(r.ptr_) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; if (ptr_)</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ptr_-&gt;AddRef();</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; template &lt;typename U&gt;</p>\n<p align=\"left\">&nbsp; scoped_refptr(const scoped_refptr&lt;U&gt;&amp; r) : ptr_(r.get()) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; if (ptr_)</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ptr_-&gt;AddRef();</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ~scoped_refptr() {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; if (ptr_)</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ptr_-&gt;Release();</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; T* get() const { return ptr_; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; // Allow scoped_refptr&lt;C&gt; to be used in boolean expression</p>\n<p align=\"left\">&nbsp; // and comparison operations.</p>\n<p align=\"left\">&nbsp; operator T*() const { return ptr_; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; T* operator-&gt;() const {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; assert(ptr_ != NULL);</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return ptr_;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; scoped_refptr&lt;T&gt;&amp; operator=(T* p) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; // AddRef first so that self assignment should work</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; if (p)</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; p-&gt;AddRef();</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; T* old_ptr = ptr_;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; ptr_ = p;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; if (old_ptr)</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; old_ptr-&gt;Release();</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return *this;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; scoped_refptr&lt;T&gt;&amp; operator=(const scoped_refptr&lt;T&gt;&amp; r) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return *this = r.ptr_;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; template &lt;typename U&gt;</p>\n<p align=\"left\">&nbsp;&nbsp;scoped_refptr&lt;T&gt;&amp; operator=(const scoped_refptr&lt;U&gt;&amp; r) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return *this = r.get();</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; void swap(T** pp) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; T* p = ptr_;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; ptr_ = *pp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; *pp = p;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; void swap(scoped_refptr&lt;T&gt;&amp; r) { swap(&amp;r.ptr_); }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;protected:</p>\n<p align=\"left\">&nbsp; T* ptr_;</p>\n<p align=\"left\">};</p>\n<h2>1.11&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CEF自定义字符串<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<h3>1.11.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 为什么自定义字符串类型<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>libcef包和宿主程序可能使用不同的运行时，对堆管理的方式也不同。所有的对象，包括字符串，需要确保和申请堆内存使用相同的运行时环境。所以需要自定义字符串类型。</p>\n<h3>1.11.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 字符串操作函数CefString<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>CEF提供了一批C语言的方法来操作字符串(通过#define的方式来适应不同的字符编码)</p>\n<p>cef_string_set 对制定的字符串变量赋值(支持深拷贝或浅拷贝)。</p>\n<p>cef_string_clear 清空字符串。</p>\n<p>cef_string_cmp 比较两个字符串。</p>\n<h3>1.11.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CEF与String的转换<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>CefString支持与std::string(UTF8)、std::wstring(wide)类型的相互转换。也可以用来包裹一个cef_string_t结构来对其进行赋值。</p>\n<p><strong>（</strong><strong>1</strong><strong>）</strong><strong>string</strong><strong>转</strong><strong>CefString</strong></p>\n<p>单字节</p>\n<p>std::string str = “Some UTF8 string”;</p>\n<p>CefString cef_str(str);</p>\n<p>cef_str = str;</p>\n<p>cef_str.FromString(str);</p>\n<p>宽字节</p>\n<p>std::wstring str = “Some wide string”;</p>\n<p>CefString cef_str(str);</p>\n<p>cef_str = str;</p>\n<p>cef_str.FromWString(str);</p>\n<p>ASCII码</p>\n<p>const char* cstr = “Some ASCII string”;</p>\n<p>CefString cef_str;</p>\n<p>cef_str.FromASCII(cstr);</p>\n<p>（2）<strong>CefString</strong><strong>转</strong><strong>string</strong></p>\n<p>单字节</p>\n<p>str = cef_str;</p>\n<p>str = cef_str.ToString();</p>\n<p>宽字节</p>\n<p>str = cef_str;</p>\n<p>str = cef_str.ToWString();</p>\n<p align=\"left\">&nbsp;</p>\n<h1>2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cef常用接口类介绍<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h1>\n<p>Cef是一个网页嵌入外壳，要获取网页的响应，拦截网页中的事件消息，CEF提供了一系列的接口回调类，提供各种事件回调函数，例如拦截获取键盘、鼠标、加载、显示、右键菜单、提示消息、状态变化、窗口打开关闭等，CEF都可以从网页上拦截捕捉消息事件，并通过回调函数传给应用程序进行处理。</p>\n<h2>2.1&nbsp; CefClient<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefClient提供了获取各种handler的接口，例如上下文菜单handler、对话框handler、显示状态handler，下载事件handler、拖动事件handler、查找事件handler、键盘handler、生命周期事件handler、加载页面事件handler、离屏render进程handler、请求事件handler等。但是只是返回事件的handle。每个handler的具体的回调函数不在CefClient类中，需要继承各个handler类，才可以实现回调。CefClient类只有一个回调函数OnProcessMessageReceived用来处理进程间的通讯消息。CefClient的定义如下：</p>\n<p align=\"left\">class CefClient : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for context menus. If no handler is provided the default</p>\n<p align=\"left\">&nbsp; // implementation will be used.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefContextMenuHandler&gt; GetContextMenuHandler() {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return NULL;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for dialogs. If no handler is provided the default</p>\n<p align=\"left\">&nbsp; // implementation will be used.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefDialogHandler&gt; GetDialogHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for browser display state events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefDisplayHandler&gt; GetDisplayHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for download events. If no handler is returned downloads</p>\n<p align=\"left\">&nbsp; // will not be allowed.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefDownloadHandler&gt; GetDownloadHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for drag events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefDragHandler&gt; GetDragHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for find result events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefFindHandler&gt; GetFindHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for focus events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefFocusHandler&gt; GetFocusHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for JavaScript dialogs. If no handler is provided the</p>\n<p align=\"left\">&nbsp; // default implementation will be used.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefJSDialogHandler&gt; GetJSDialogHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for keyboard events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefKeyboardHandler&gt; GetKeyboardHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for browser life span events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefLifeSpanHandler&gt; GetLifeSpanHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for browser load status events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefLoadHandler&gt; GetLoadHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for off-screen rendering events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefRenderHandler&gt; GetRenderHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Return the handler for browser request events.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefRequestHandler&gt; GetRequestHandler() { return NULL; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when a new message is received from a different process. Return true</p>\n<p align=\"left\">&nbsp; // if the message was handled or false otherwise. Do not keep a reference to</p>\n<p align=\"left\">&nbsp; // or attempt to access the message outside of this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnProcessMessageReceived(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefProcessId source_process,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefProcessMessage&gt; message) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p>};</p>\n<h2>2.2&nbsp; CefContextMenuHandler右键菜单处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefContextMenuHandler是网页上的右键菜单事件回调函数类，提供OnBeforeContextMenu回调函数，在右键菜单弹出之前修改或者禁用右键菜单。右键菜单按钮响应回调RunContextMenu、右键菜单命令回调OnContextMenuCommand菜单禁用回调函数OnContextMenuDismissed等。可以拦截右键菜单响应，进行自定义的处理。</p>\n<p align=\"left\">class CefContextMenuHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; typedef cef_event_flags_t EventFlags;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called before a context menu is displayed. |params| provides information</p>\n<p align=\"left\">&nbsp; // about the context menu state. |model| initially contains the default</p>\n<p align=\"left\">&nbsp; // context menu. The |model| can be cleared to show no context menu or</p>\n<p align=\"left\">&nbsp; // modified to show a custom menu. Do not keep references to |params| or</p>\n<p align=\"left\">&nbsp; // |model| outside of this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnBeforeContextMenu(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefContextMenuParams&gt; params,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefMenuModel&gt; model) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called to allow custom display of the context menu. |params| provides</p>\n<p align=\"left\">&nbsp; // information about the context menu state. |model| contains the context menu</p>\n<p align=\"left\">&nbsp; // model resulting from OnBeforeContextMenu. For custom display return true</p>\n<p align=\"left\">&nbsp; // and execute |callback| either synchronously or asynchronously with the</p>\n<p align=\"left\">&nbsp; // selected command ID. For default display return false. Do not keep</p>\n<p align=\"left\">&nbsp; // references to |params| or |model| outside of this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool RunContextMenu(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefContextMenuParams&gt; params,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefMenuModel&gt; model,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRunContextMenuCallback&gt; callback) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called to execute a command selected from the context menu. Return true if</p>\n<p align=\"left\">&nbsp; // the command was handled or false for the default implementation. See</p>\n<p align=\"left\">&nbsp; // cef_menu_id_t for the command ids that have default implementations. All</p>\n<p align=\"left\">&nbsp; // user-defined command ids should be between MENU_ID_USER_FIRST and</p>\n<p align=\"left\">&nbsp; // MENU_ID_USER_LAST. |params| will have the same values as what was passed to</p>\n<p align=\"left\">&nbsp; // OnBeforeContextMenu(). Do not keep a reference to |params| outside of this</p>\n<p align=\"left\">&nbsp; // callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnContextMenuCommand(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefContextMenuParams&gt; params,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int command_id,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;EventFlags event_flags) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the context menu is dismissed irregardless of whether the menu</p>\n<p align=\"left\">&nbsp; // was empty or a command was selected.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnContextMenuDismissed(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame) {}</p>\n<p>};</p>\n<p>&nbsp;</p>\n<h2>2.3&nbsp; CefDisplayHandler网页显示处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefDisplayHandler提供了一些页面显示回调函数，例如网址发送变化OnAddressChange，网页标题OnTitleChange发生变化，网页图标发生变化OnFaviconURLChange，全屏变化OnFullscreenModeChange，显示提示消息OnTooltip，状态栏消息显示OnStatusMessage，控制台消息回调OnConsoleMessage，设置了自动调整大小回调OnAutoResize，加载进程变化回调OnLoadingProgressChange，CefDisplayHandler类定义如下</p>\n<p align=\"left\">class CefDisplayHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when a frame\'s address has changed.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnAddressChange(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; url) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the page title changes.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=title)--*/</p>\n<p align=\"left\">&nbsp; virtual void OnTitleChange(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; title) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the page icon changes.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=icon_urls)--*/</p>\n<p align=\"left\">&nbsp; virtual void OnFaviconURLChange(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const std::vector&lt;CefString&gt;&amp; icon_urls) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when web content in the page has toggled fullscreen mode. If</p>\n<p align=\"left\">&nbsp; // |fullscreen| is true the content will automatically be sized to fill the</p>\n<p align=\"left\">&nbsp; // browser content area. If |fullscreen| is false the content will</p>\n<p align=\"left\">&nbsp; // automatically return to its original size and position. The client is</p>\n<p align=\"left\">&nbsp; // responsible for resizing the browser if desired.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnFullscreenModeChange(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool fullscreen) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the browser is about to display a tooltip. |text| contains the</p>\n<p align=\"left\">&nbsp; // text that will be displayed in the tooltip. To handle the display of the</p>\n<p align=\"left\">&nbsp; // tooltip yourself return true. Otherwise, you can optionally modify |text|</p>\n<p align=\"left\">&nbsp; // and then return false to allow the browser to display the tooltip.</p>\n<p align=\"left\">&nbsp; // When window rendering is disabled the application is responsible for</p>\n<p align=\"left\">&nbsp; // drawing tooltips and the return value is ignored.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=text)--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnTooltip(CefRefPtr&lt;CefBrowser&gt; browser, CefString&amp; text) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the browser receives a status message. |value| contains the</p>\n<p align=\"left\">&nbsp; // text that will be displayed in the status message.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=value)--*/</p>\n<p align=\"left\">&nbsp; virtual void OnStatusMessage(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; value) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called to display a console message. Return true to stop the message from</p>\n<p align=\"left\">&nbsp; // being output to the console.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=message,optional_param=source)--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnConsoleMessage(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;cef_log_severity_t level,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; message,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; source,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int line) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when auto-resize is enabled via CefBrowserHost::SetAutoResizeEnabled</p>\n<p align=\"left\">&nbsp; // and the contents have auto-resized. |new_size| will be the desired size in</p>\n<p align=\"left\">&nbsp; // view coordinates. Return true if the resize was handled or false for</p>\n<p align=\"left\">&nbsp; // default handling.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnAutoResize(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefSize&amp; new_size) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the overall page loading progress has changed. |progress|</p>\n<p align=\"left\">&nbsp; // ranges from 0.0 to 1.0.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnLoadingProgressChange(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; double progress) {}</p>\n<p>};</p>\n<h2>2.4&nbsp; CefDownloadHandler网页下载处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefDownloadHandler网页上下载文件类，提供开始从网页下载文件回调函数OnBeforeDownload，下载文件进度更新回调函数OnDownloadUpdated。</p>\n<p align=\"left\">class CefDownloadHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called before a download begins. |suggested_name| is the suggested name for</p>\n<p align=\"left\">&nbsp; // the download file. By default the download will be canceled. Execute</p>\n<p align=\"left\">&nbsp; // |callback| either asynchronously or in this method to continue the download</p>\n<p align=\"left\">&nbsp; // if desired. Do not keep a reference to |download_item| outside of this</p>\n<p align=\"left\">&nbsp; // method.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnBeforeDownload(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefDownloadItem&gt; download_item,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; suggested_name,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBeforeDownloadCallback&gt; callback) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when a download\'s status or progress information has been updated.</p>\n<p align=\"left\">&nbsp; // This may be called multiple times before and after OnBeforeDownload().</p>\n<p align=\"left\">&nbsp; // Execute |callback| either asynchronously or in this method to cancel the</p>\n<p align=\"left\">&nbsp; // download if desired. Do not keep a reference to |download_item| outside of</p>\n<p align=\"left\">&nbsp; // this method.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnDownloadUpdated(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefDownloadItem&gt; download_item,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefDownloadItemCallback&gt; callback) {}</p>\n<p align=\"left\">};</p>\n<p>&nbsp;</p>\n<h2>2.5&nbsp; CefDragHandler鼠标拖动到网页处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefDragHandler处理鼠标拖动事件，提供鼠标拖动进入网页回调函数OnDragEnter，网页中可以拖动放入的区域发生变化回调函数OnDraggableRegionsChanged。</p>\n<p align=\"left\">// Implement this interface to handle events related to dragging. The methods of</p>\n<p align=\"left\">// this class will be called on the UI thread.</p>\n<p align=\"left\">///</p>\n<p align=\"left\">/*--cef(source=client)--*/</p>\n<p align=\"left\">class CefDragHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; typedef cef_drag_operations_mask_t DragOperationsMask;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when an external drag event enters the browser window. |dragData|</p>\n<p align=\"left\">&nbsp; // contains the drag event data and |mask| represents the type of drag</p>\n<p align=\"left\">&nbsp; // operation. Return false for default drag handling behavior or true to</p>\n<p align=\"left\">&nbsp; // cancel the drag event.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnDragEnter(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefDragData&gt; dragData,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; DragOperationsMask mask) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called whenever draggable regions for the browser window change. These can</p>\n<p align=\"left\">&nbsp; // be specified using the \'-webkit-app-region: drag/no-drag\' CSS-property. If</p>\n<p align=\"left\">&nbsp; // draggable regions are never defined in a document this method will also</p>\n<p align=\"left\">&nbsp; // never be called. If the last draggable region is removed from a document</p>\n<p align=\"left\">&nbsp; // this method will be called with an empty vector.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnDraggableRegionsChanged(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const std::vector&lt;CefDraggableRegion&gt;&amp; regions) {}</p>\n<p align=\"left\">};</p>\n<p>&nbsp;</p>\n<h2>2.6&nbsp; CefKeyboardHandler键盘事件响应处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefKeyboardHandler处理键盘响应事件，提供键盘按键响应回调函数。拦截键盘消息。</p>\n<p align=\"left\">// Implement this interface to handle events related to keyboard input. The</p>\n<p align=\"left\">// methods of this class will be called on the UI thread.</p>\n<p align=\"left\">///</p>\n<p align=\"left\">/*--cef(source=client)--*/</p>\n<p align=\"left\">class CefKeyboardHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called before a keyboard event is sent to the renderer. |event| contains</p>\n<p align=\"left\">&nbsp; // information about the keyboard event. |os_event| is the operating system</p>\n<p align=\"left\">&nbsp; // event message, if any. Return true if the event was handled or false</p>\n<p align=\"left\">&nbsp; // otherwise. If the event will be handled in OnKeyEvent() as a keyboard</p>\n<p align=\"left\">&nbsp; // shortcut set |is_keyboard_shortcut| to true and return false.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnPreKeyEvent(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefKeyEvent&amp; event,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefEventHandle os_event,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;bool* is_keyboard_shortcut) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called after the renderer and JavaScript in the page has had a chance to</p>\n<p align=\"left\">&nbsp; // handle the event. |event| contains information about the keyboard event.</p>\n<p align=\"left\">&nbsp; // |os_event| is the operating system event message, if any. Return true if</p>\n<p align=\"left\">&nbsp; // the keyboard event was handled or false otherwise.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnKeyEvent(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefKeyEvent&amp; event,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;CefEventHandle os_event) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">};</p>\n<h2>2.7&nbsp; CefLifeSpanHandler生命周期处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>CefLifeSpanHandler是生命周期处理类，新打开一个网页或者关闭一个网页时，会触发回调函数。OnBeforePopup这个只能在创建一个新的弹出式网页时，才会触发，如果是在一个网页中打开一个子网页，回调函数是拦截不到消息的。OnAfterCreated网页创建完成后的回调函数。browser销毁之前会触发回调函数OnBeforeClose。还有一个关闭回调函数DoClose有点复杂，当调用CefBrowserHost::*CloseBrowser()函数关闭browser，或者browser是CEF创建的顶层窗口的子窗口，当顶层窗口关闭时，也会触发关闭DoClose回调函数。点击网页的关闭按钮后，网页不会立刻关闭，而是会调用两次CloseBrowser()或TryCloseBrowser()，提供了一个让CEF处理JS的onbeforeunload事件和选择性取消关闭网页的机会。CefLifeSpanHandler类定义如下：</p>\n<p>&nbsp;</p>\n<p align=\"left\">// Implement this interface to handle events related to browser life span. The</p>\n<p align=\"left\">// methods of this class will be called on the UI thread unless otherwise</p>\n<p align=\"left\">// indicated.</p>\n<p align=\"left\">///</p>\n<p align=\"left\">/*--cef(source=client)--*/</p>\n<p align=\"left\">class CefLifeSpanHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; typedef cef_window_open_disposition_t WindowOpenDisposition;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the UI thread before a new popup browser is created. The</p>\n<p align=\"left\">&nbsp; // |browser| and |frame| values represent the source of the popup request. The</p>\n<p align=\"left\">&nbsp; // |target_url| and |target_frame_name| values indicate where the popup</p>\n<p align=\"left\">&nbsp; // browser should navigate and may be empty if not specified with the request.</p>\n<p align=\"left\">&nbsp; // The |target_disposition| value indicates where the user intended to open</p>\n<p align=\"left\">&nbsp; // the popup (e.g. current tab, new tab, etc). The |user_gesture| value will</p>\n<p align=\"left\">&nbsp; // be true if the popup was opened via explicit user gesture (e.g. clicking a</p>\n<p align=\"left\">&nbsp; // link) or false if the popup opened automatically (e.g. via the</p>\n<p align=\"left\">&nbsp; // DomContentLoaded event). The |popupFeatures| structure contains additional</p>\n<p align=\"left\">&nbsp; // information about the requested popup window. To allow creation of the</p>\n<p align=\"left\">&nbsp; // popup browser optionally modify |windowInfo|, |client|, |settings| and</p>\n<p align=\"left\">&nbsp; // |no_javascript_access| and return false. To cancel creation of the popup</p>\n<p align=\"left\">&nbsp; // browser return true. The |client| and |settings| values will default to the</p>\n<p align=\"left\">&nbsp; // source browser\'s values. If the |no_javascript_access| value is set to</p>\n<p align=\"left\">&nbsp; // false the new browser will not be scriptable and may not be hosted in the</p>\n<p align=\"left\">&nbsp; // same renderer process as the source browser. Any modifications to</p>\n<p align=\"left\">&nbsp; // |windowInfo| will be ignored if the parent browser is wrapped in a</p>\n<p align=\"left\">&nbsp; // CefBrowserView. Popup browser creation will be canceled if the parent</p>\n<p align=\"left\">&nbsp; // browser is destroyed before the popup browser creation completes (indicated</p>\n<p align=\"left\">&nbsp; // by a call to OnAfterCreated for the popup browser).</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=target_url,optional_param=target_frame_name)--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnBeforePopup(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; target_url,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;const CefString&amp; target_frame_name,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; WindowOpenDisposition target_disposition,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool user_gesture,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefPopupFeatures&amp; popupFeatures,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;CefWindowInfo&amp; windowInfo,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefClient&gt;&amp; client,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefBrowserSettings&amp; settings,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool* no_javascript_access) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called after a new browser is created. This callback will be the first</p>\n<p align=\"left\">&nbsp; // notification that references |browser|.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnAfterCreated(CefRefPtr&lt;CefBrowser&gt; browser) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when a browser has recieved a request to close. This may result</p>\n<p align=\"left\">&nbsp; // directly from a call to CefBrowserHost::*CloseBrowser() or indirectly if</p>\n<p align=\"left\">&nbsp; // the browser is parented to a top-level window created by CEF and the user</p>\n<p align=\"left\">&nbsp; // attempts to close that window (by clicking the \'X\', for example). The</p>\n<p align=\"left\">&nbsp; // DoClose() method will be called after the JavaScript \'onunload\' event has</p>\n<p align=\"left\">&nbsp; // been fired.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // An application should handle top-level owner window close notifications by</p>\n<p align=\"left\">&nbsp; // calling CefBrowserHost::TryCloseBrowser() or</p>\n<p align=\"left\">&nbsp; // CefBrowserHost::CloseBrowser(false) instead of allowing the window to close</p>\n<p align=\"left\">&nbsp; // immediately (see the examples below). This gives CEF an opportunity to</p>\n<p align=\"left\">&nbsp; // process the \'onbeforeunload\' event and optionally cancel the close before</p>\n<p align=\"left\">&nbsp; // DoClose() is called.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // When windowed rendering is enabled CEF will internally create a window or</p>\n<p align=\"left\">&nbsp; // view to host the browser. In that case returning false from DoClose() will</p>\n<p align=\"left\">&nbsp; // send the standard close notification to the browser\'s top-level owner</p>\n<p align=\"left\">&nbsp; // window (e.g. WM_CLOSE on Windows, performClose: on OS X, \"delete_event\" on</p>\n<p align=\"left\">&nbsp; // Linux or CefWindowDelegate::CanClose() callback from Views). If the</p>\n<p align=\"left\">&nbsp; // browser\'s host window/view has already been destroyed (via view hierarchy</p>\n<p align=\"left\">&nbsp; // tear-down, for example) then DoClose() will not be called for that browser</p>\n<p align=\"left\">&nbsp; // since is no longer possible to cancel the close.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // When windowed rendering is disabled returning false from DoClose() will</p>\n<p align=\"left\">&nbsp; // cause the browser object to be destroyed immediately.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // If the browser\'s top-level owner window requires a non-standard close</p>\n<p align=\"left\">&nbsp; // notification then send that notification from DoClose() and return true.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // The CefLifeSpanHandler::OnBeforeClose() method will be called after</p>\n<p align=\"left\">&nbsp; // DoClose() (if DoClose() is called) and immediately before the browser</p>\n<p align=\"left\">&nbsp; // object is destroyed. The application should only exit after OnBeforeClose()</p>\n<p align=\"left\">&nbsp; // has been called for all existing browsers.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // The below examples describe what should happen during window close when the</p>\n<p align=\"left\">&nbsp; // browser is parented to an application-provided top-level window.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // Example 1: Using CefBrowserHost::TryCloseBrowser(). This is recommended for</p>\n<p align=\"left\">&nbsp; // clients using standard close handling and windows created on the browser</p>\n<p align=\"left\">&nbsp; // process UI thread.</p>\n<p align=\"left\">&nbsp; // 1.&nbsp; User clicks the window close button which sends a close notification to</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; the application\'s top-level window.</p>\n<p align=\"left\">&nbsp; // 2.&nbsp; Application\'s top-level window receives the close notification and</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; calls TryCloseBrowser() (which internally calls CloseBrowser(false)).</p>\n<p align=\"left\">&nbsp; // &nbsp;&nbsp;&nbsp;&nbsp;TryCloseBrowser() returns false so the client cancels the window close.</p>\n<p align=\"left\">&nbsp; // 3.&nbsp; JavaScript \'onbeforeunload\' handler executes and shows the close</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; confirmation dialog (which can be overridden via</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; CefJSDialogHandler::OnBeforeUnloadDialog()).</p>\n<p align=\"left\">&nbsp; // 4.&nbsp; User approves the close.</p>\n<p align=\"left\">&nbsp; // 5.&nbsp; JavaScript \'onunload\' handler executes.</p>\n<p align=\"left\">&nbsp; // 6.&nbsp; CEF sends a close notification to the application\'s top-level window</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; (because DoClose() returned false by default).</p>\n<p align=\"left\">&nbsp; // 7.&nbsp; Application\'s top-level window receives the close notification and</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; calls TryCloseBrowser(). TryCloseBrowser() returns true so the client</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; allows the window close.</p>\n<p align=\"left\">&nbsp; // 8.&nbsp; Application\'s top-level window is destroyed.</p>\n<p align=\"left\">&nbsp; // 9.&nbsp; Application\'s OnBeforeClose() handler is called and the browser object</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; is destroyed.</p>\n<p align=\"left\">&nbsp; // 10. Application exits by calling CefQuitMessageLoop() if no other browsers</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; exist.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; // Example 2: Using CefBrowserHost::CloseBrowser(false) and implementing the</p>\n<p align=\"left\">&nbsp; // DoClose() callback. This is recommended for clients using non-standard</p>\n<p align=\"left\">&nbsp; // close handling or windows that were not created on the browser process UI</p>\n<p align=\"left\">&nbsp; // thread.</p>\n<p align=\"left\">&nbsp; // 1.&nbsp; User clicks the window close button which sends a close notification to</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; the application\'s top-level window.</p>\n<p align=\"left\">&nbsp; // 2.&nbsp; Application\'s top-level window receives the close notification and:</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; A. Calls CefBrowserHost::CloseBrowser(false).</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; B. Cancels the window close.</p>\n<p align=\"left\">&nbsp; // 3.&nbsp; JavaScript \'onbeforeunload\' handler executes and shows the close</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; confirmation dialog (which can be overridden via</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; CefJSDialogHandler::OnBeforeUnloadDialog()).</p>\n<p align=\"left\">&nbsp; // 4.&nbsp; User approves the close.</p>\n<p align=\"left\">&nbsp; // 5.&nbsp; JavaScript \'onunload\' handler executes.</p>\n<p align=\"left\">&nbsp; // 6.&nbsp; Application\'s DoClose() handler is called. Application will:</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; A. Set a flag to indicate that the next close attempt will be allowed.</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; B. Return false.</p>\n<p align=\"left\">&nbsp; // 7.&nbsp; CEF sends an close notification to the application\'s top-level window.</p>\n<p align=\"left\">&nbsp; // 8.&nbsp; Application\'s top-level window receives the close notification and</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; allows the window to close based on the flag from #6B.</p>\n<p align=\"left\">&nbsp; // 9.&nbsp; Application\'s top-level window is destroyed.</p>\n<p align=\"left\">&nbsp; // 10. Application\'s OnBeforeClose() handler is called and the browser object</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; is destroyed.</p>\n<p align=\"left\">&nbsp; // 11. Application exits by calling CefQuitMessageLoop() if no other browsers</p>\n<p align=\"left\">&nbsp; //&nbsp;&nbsp;&nbsp;&nbsp; exist.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool DoClose(CefRefPtr&lt;CefBrowser&gt; browser) { return false; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called just before a browser is destroyed. Release all references to the</p>\n<p align=\"left\">&nbsp; // browser object and do not attempt to execute any methods on the browser</p>\n<p align=\"left\">&nbsp; // object after this callback returns. This callback will be the last</p>\n<p align=\"left\">&nbsp; // notification that references |browser|. See DoClose() documentation for</p>\n<p align=\"left\">&nbsp; // additional usage information.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnBeforeClose(CefRefPtr&lt;CefBrowser&gt; browser) {}</p>\n<p>};</p>\n<p>&nbsp;</p>\n<h3>2.7.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; DoClose标准关闭处理<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>当窗口创建是在browser进程的UI线程创建时，采用标准的关闭处理，使用CefBrowserHost::TryCloseBrowser()。不实现DoClose回调，默认返回false。具体步骤：</p>\n<p>（1）&nbsp;&nbsp; 点击窗口的关闭按钮，发送一个关闭通知给顶层窗口。</p>\n<p>（2）&nbsp;&nbsp; 顶层窗口接收到关闭通知，调用TryCloseBrowser()函数，返回false；</p>\n<p>（3）&nbsp;&nbsp; JS的onbeforeunload处理句柄执行显示关闭确认对话框。</p>\n<p>（4）&nbsp;&nbsp; 用户点击按钮同意关闭；</p>\n<p>（5）&nbsp;&nbsp; JS的onunload处理句柄执行；</p>\n<p>（6）&nbsp;&nbsp; CEF发送一个close通知给顶层窗口；</p>\n<p>（7）&nbsp;&nbsp; 定鞥窗口接收到关闭通知，调用TryCloseBrowser，返回true，表示允许关闭。</p>\n<p>（8）&nbsp;&nbsp; 顶层窗口销毁</p>\n<p>（9）&nbsp;&nbsp; 程序的OnBeforeClose处理回调函数执行，browser销毁.</p>\n<p>（10）如果不存在其他browser，则调用CefQuitMessageLoop退出程序。</p>\n<h3>2.7.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; DoClose非标准关闭处理<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>当窗口不是在browser进程的UI线程中创建时，采用非标准的关闭处理，使用函数CefBrowserHost::CloseBrowser(false)，并且实现DoClose函数。</p>\n<p>（1）&nbsp;&nbsp; 用户点击窗口的关闭按钮，发送一个关闭通知给顶层窗口。</p>\n<p><strong>（2）&nbsp;&nbsp;&nbsp;</strong><strong>顶层窗口接收到关闭通知，调用</strong><strong>CefBrowserHost::CloseBrowser(false)</strong><strong>函数，取消关闭；</strong></p>\n<p>（3）&nbsp;&nbsp; JS的onbeforeunload处理句柄执行显示关闭确认对话框。</p>\n<p>（4）&nbsp;&nbsp; 用户点击按钮同意关闭；</p>\n<p>（5）&nbsp;&nbsp; JS的onunload处理句柄执行；</p>\n<p>（6）&nbsp;&nbsp; 程序的DoClose（）回调函数被调用，设置一个flag表明下次关闭尝试会被允许，返回false；</p>\n<p>（7）&nbsp;&nbsp; CEF发送一个close通知给顶层窗口；</p>\n<p><strong>（8）&nbsp;&nbsp;&nbsp;</strong><strong>顶层窗口接收到关闭通知，根据之前设置的flag</strong><strong>判断是否关闭窗口。</strong></p>\n<p>（9）&nbsp;&nbsp; 顶层窗口销毁；</p>\n<p>（10）程序的OnBeforeClose处理回调函数执行，browser销毁.</p>\n<p>（11）如果不存在其他browser，则调用CefQuitMessageLoop退出程序。</p>\n<p>&nbsp;</p>\n<h2>2.8&nbsp; CefLoadHandler网页加载处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>在一个网页中加载内容，或者在网页中打开一个子frame，都可以拦截到iframe打开时的消息以及url等信息。可以拦截子网页url</p>\n<p>（1）&nbsp;&nbsp; 开始加载OnLoadStart，navigation执行网之后，开始加载内容之前，回调此函数，多frame的进程会同时加载。同页面巡航不会调用。</p>\n<p>（2）&nbsp;&nbsp; 加载结束OnLoadEnd，加载结束时回调，sub-frame在主frame加载结束后， 会继续开始加载或继续进行加载，同页面巡航不会调用。</p>\n<p>（3）&nbsp;&nbsp; 加载错误OnLoadError，navigation失败或者取消是回调。</p>\n<p>（4）&nbsp;&nbsp; 加载状态发生变化OnLoadingStateChange，加载初始化和加载结束时各调用一次，在OnLoadStart之前调用一次，OnLoadEnd或OnLoadError之后调用一次。</p>\n<p>&nbsp;</p>\n<p align=\"left\">// Implement this interface to handle events related to browser load status. The</p>\n<p align=\"left\">// methods of this class will be called on the browser process UI thread or</p>\n<p align=\"left\">// render process main thread (TID_RENDERER).</p>\n<p align=\"left\">///</p>\n<p align=\"left\">/*--cef(source=client)--*/</p>\n<p align=\"left\">class CefLoadHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; typedef cef_errorcode_t ErrorCode;</p>\n<p align=\"left\">&nbsp; typedef cef_transition_type_t TransitionType;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the loading state has changed. This callback will be executed</p>\n<p align=\"left\">&nbsp; // twice -- once when loading is initiated either programmatically or by user</p>\n<p align=\"left\">&nbsp; // action, and once when loading is terminated due to completion, cancellation</p>\n<p align=\"left\">&nbsp; // of failure. It will be called before any calls to OnLoadStart and after all</p>\n<p align=\"left\">&nbsp; // calls to OnLoadError and/or OnLoadEnd.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnLoadingStateChange(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool isLoading,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool canGoBack,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool canGoForward) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called after a navigation has been committed and before the browser begins</p>\n<p align=\"left\">&nbsp; // loading contents in the frame. The |frame| value will never be empty --</p>\n<p align=\"left\">&nbsp; // call the IsMain() method to check if this frame is the main frame.</p>\n<p align=\"left\">&nbsp; // |transition_type| provides information about the source of the navigation</p>\n<p align=\"left\">&nbsp; // and an accurate value is only available in the browser process. Multiple</p>\n<p align=\"left\">&nbsp; // frames may be loading at the same time. Sub-frames may start or continue</p>\n<p align=\"left\">&nbsp; // loading after the main frame load has ended. This method will not be called</p>\n<p align=\"left\">&nbsp; // for same page navigations (fragments, history state, etc.) or for</p>\n<p align=\"left\">&nbsp; // navigations that fail or are canceled before commit. For notification of</p>\n<p align=\"left\">&nbsp; // overall browser load status use OnLoadingStateChange instead.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnLoadStart(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TransitionType transition_type) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when the browser is done loading a frame. The |frame| value will</p>\n<p align=\"left\">&nbsp; // never be empty -- call the IsMain() method to check if this frame is the</p>\n<p align=\"left\">&nbsp; // main frame. Multiple frames may be loading at the same time. Sub-frames may</p>\n<p align=\"left\">&nbsp; // start or continue loading after the main frame load has ended. This method</p>\n<p align=\"left\">&nbsp; // will not be called for same page navigations (fragments, history state,</p>\n<p align=\"left\">&nbsp; // etc.) or for navigations that fail or are canceled before commit. For</p>\n<p align=\"left\">&nbsp; // notification of overall browser load status use OnLoadingStateChange</p>\n<p align=\"left\">&nbsp; // instead.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnLoadEnd(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int httpStatusCode) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called when a navigation fails or is canceled. This method may be called</p>\n<p align=\"left\">&nbsp; // by itself if before commit or in combination with OnLoadStart/OnLoadEnd if</p>\n<p align=\"left\">&nbsp; // after commit. |errorCode| is the error code number, |errorText| is the</p>\n<p align=\"left\">&nbsp; // error text and |failedUrl| is the URL that failed to load.</p>\n<p align=\"left\">&nbsp; // See net\\base\\net_error_list.h for complete descriptions of the error codes.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=errorText)--*/</p>\n<p align=\"left\">&nbsp; virtual void OnLoadError(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; ErrorCode errorCode,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; errorText,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; failedUrl) {}</p>\n<p align=\"left\">};</p>\n<p align=\"left\">&nbsp;</p>\n<h2>2.9&nbsp; CefRequestHandler网络请求处理类<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>当打开一个网页， CefRequestHandler的OnBeforeBrowser可以拦截网络请求，只有在新打开网页的时候，才会触发，如果网页已经打开，在网页内部点击查询按钮，查询内容，虽然也有request请求，但是OnBeforeBrowser拦截不到获取请求的URL，post请求的参数都可以获取到。OnResourceRedirect还可以拦截重定向请求。CefLoadHandler也可以拦截request请求，而且页面加载中调用很多的GET和POST请求都可以拦截到。测试发现CefRequestHandler页面内部的加载变化是获取不到的，只有打开页面的请求能获取到。而另外一个函数OnBeforeResourceLoad则可以拦截所有的请求，在浏览器中F12显示的所有请求，包括图片下载等请求都能一一获取。所以CefLoadHandler拦截的请求更详细一些，点击查询查询，OnLoadStart和OnLoadEnd 拦截不到，但是OnLoadingStateChange 可以拦截的到请求。</p>\n<div class=\"table-wrapper\">\n<div class=\"table-wrapper\"><table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\n<tbody>\n<tr>\n<td valign=\"top\" width=\"191\">\n<p>OnBeforeBrowser</p>\n</td>\n<td valign=\"top\" width=\"280\">\n<p>打开新的网页可以拦截，页面内容变化，或者页面内部调用请求拦截不到。</p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"191\">\n<p>OnBeforeResourceLoad</p>\n</td>\n<td valign=\"top\" width=\"280\">\n<p>拦截一切请求，最详细。</p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"191\">\n<p>OnResourceResponse</p>\n</td>\n<td valign=\"top\" width=\"280\">\n<p>拦截一切请求，最详细。</p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"191\">\n<p>&nbsp;</p>\n</td>\n<td valign=\"top\" width=\"280\">\n<p>&nbsp;</p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"191\">\n<p>OnLoadStart和OnLoadEnd</p>\n</td>\n<td valign=\"top\" width=\"280\">\n<p>新打开页面可以拦截</p>\n</td>\n</tr>\n<tr>\n<td valign=\"top\" width=\"191\">\n<p>OnLoadingStateChange</p>\n</td>\n<td valign=\"top\" width=\"280\">\n<p>打开新页面， 页面内容重新加载，查询，按钮响应可以拦截。像一些图片加载，CSS加载是拦截不到的。第二详细。</p>\n</td>\n</tr>\n</tbody>\n</table></div>\n</div>\n<p>&nbsp;</p>\n<p>各回调函的调用的先后顺序是</p>\n<p>OnLoadingStateChange-&gt;OnBeforeBrowser-&gt;OnLoadStart-&gt;OnLoadEnd-&gt;OnLoadingStateChange。</p>\n<p align=\"left\">// Implement this interface to handle events related to browser requests. The</p>\n<p align=\"left\">// methods of this class will be called on the thread indicated.</p>\n<p align=\"left\">///</p>\n<p align=\"left\">/*--cef(source=client)--*/</p>\n<p align=\"left\">class CefRequestHandler : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; typedef cef_return_value_t ReturnValue;</p>\n<p align=\"left\">&nbsp; typedef cef_termination_status_t TerminationStatus;</p>\n<p align=\"left\">&nbsp; typedef cef_urlrequest_status_t URLRequestStatus;</p>\n<p align=\"left\">&nbsp; typedef cef_window_open_disposition_t WindowOpenDisposition;</p>\n<p align=\"left\">&nbsp; typedef std::<em>vector</em>&lt;CefRefPtr&lt;CefX509Certificate&gt;&gt; X509CertificateList;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the UI thread before browser navigation. Return true to cancel</p>\n<p align=\"left\">&nbsp; // the navigation or false to allow the navigation to proceed. The |request|</p>\n<p align=\"left\">&nbsp; // object cannot be modified in this callback.</p>\n<p align=\"left\">&nbsp; // CefLoadHandler::OnLoadingStateChange will be called twice in all cases.</p>\n<p align=\"left\">&nbsp; // If the navigation is allowed CefLoadHandler::OnLoadStart and</p>\n<p align=\"left\">&nbsp; // CefLoadHandler::OnLoadEnd will be called. If the navigation is canceled</p>\n<p align=\"left\">&nbsp; // CefLoadHandler::OnLoadError will be called with an |errorCode| value of</p>\n<p align=\"left\">&nbsp; // ERR_ABORTED. The |user_gesture| value will be true if the browser</p>\n<p align=\"left\">&nbsp; // navigated via explicit user gesture (e.g. clicking a link) or false if it</p>\n<p align=\"left\">&nbsp; // navigated automatically (e.g. via the DomContentLoaded event).</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p>（1）OnBeforeBrowse，在浏览器巡航前调用。</p>\n<p align=\"left\">&nbsp; virtual bool OnBeforeBrowse(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool user_gesture,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool is_redirect) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the UI thread before OnBeforeBrowse in certain limited cases</p>\n<p align=\"left\">&nbsp; // where navigating a new or different browser might be desirable. This</p>\n<p align=\"left\">&nbsp; // includes user-initiated navigation that might open in a special way (e.g.</p>\n<p align=\"left\">&nbsp; // links clicked via middle-click or ctrl + left-click) and certain types of</p>\n<p align=\"left\">&nbsp; // cross-origin navigation initiated from the renderer process (e.g.</p>\n<p align=\"left\">&nbsp; // navigating the top-level frame to/from a file URL). The |browser| and</p>\n<p align=\"left\">&nbsp; // |frame| values represent the source of the navigation. The</p>\n<p align=\"left\">&nbsp; // |target_disposition| value indicates where the user intended to navigate</p>\n<p align=\"left\">&nbsp; // the browser based on standard Chromium behaviors (e.g. current tab,</p>\n<p align=\"left\">&nbsp; // new tab, etc). The |user_gesture| value will be true if the browser</p>\n<p align=\"left\">&nbsp; // navigated via explicit user gesture (e.g. clicking a link) or false if it</p>\n<p align=\"left\">&nbsp; // navigated automatically (e.g. via the DomContentLoaded event). Return true</p>\n<p align=\"left\">&nbsp; // to cancel the navigation or false to allow the navigation to proceed in the</p>\n<p align=\"left\">&nbsp; // source browser\'s top-level frame.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p>（2）OnOpenURLFromTab，以特殊的方式打开的网页，例如鼠标中间按钮，快捷键等，一些很少的应用场景。</p>\n<p align=\"left\">&nbsp; virtual bool OnOpenURLFromTab(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; target_url,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;WindowOpenDisposition target_disposition,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool user_gesture) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread before a resource request is loaded. The |request|</p>\n<p align=\"left\">&nbsp; // object may be modified. Return RV_CONTINUE to continue the request</p>\n<p align=\"left\">&nbsp; // immediately. Return RV_CONTINUE_ASYNC and call CefRequestCallback::</p>\n<p align=\"left\">&nbsp; // Continue() at a later time to continue or cancel the request</p>\n<p align=\"left\">&nbsp; // asynchronously. Return RV_CANCEL to cancel the request immediately.</p>\n<p align=\"left\">&nbsp; //</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(default_retval=RV_CONTINUE)--*/</p>\n<p>（3）OnBeforeResourceLoad网页开始加载资源时调用，可以拦截所有的请求，最为详细。</p>\n<p align=\"left\">&nbsp; virtual ReturnValue OnBeforeResourceLoad(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequestCallback&gt; callback) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return RV_CONTINUE;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">};</p>\n<p align=\"left\">///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread before a resource is loaded. To allow the resource</p>\n<p align=\"left\">&nbsp; // to load normally return NULL. To specify a handler for the resource return</p>\n<p align=\"left\">&nbsp; // a CefResourceHandler object. The |request| object should not be modified in</p>\n<p align=\"left\">&nbsp; // this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefResourceHandler&gt; GetResourceHandler(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return NULL;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread when a resource load is redirected. The |request|</p>\n<p align=\"left\">&nbsp; // parameter will contain the old URL and other request-related information.</p>\n<p align=\"left\">&nbsp; // The |response| parameter will contain the response that resulted in the</p>\n<p align=\"left\">&nbsp; // redirect. The |new_url| parameter will contain the new URL and can be</p>\n<p align=\"left\">&nbsp; // changed if desired. The |request| object cannot be modified in this</p>\n<p align=\"left\">&nbsp; // callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p>（4）OnResourceRedirect重定向请求拦截</p>\n<p align=\"left\">&nbsp; virtual void OnResourceRedirect(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefResponse&gt; response,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; new_url) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread when a resource response is received. To allow the</p>\n<p align=\"left\">&nbsp; // resource to load normally return false. To redirect or retry the resource</p>\n<p align=\"left\">&nbsp; // modify |request| (url, headers or post body) and return true. The</p>\n<p align=\"left\">&nbsp; // |response| object cannot be modified in this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p>（5）OnResourceResponse请求响应后的回调函数</p>\n<p align=\"left\">&nbsp; virtual bool OnResourceResponse(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;CefRefPtr&lt;CefResponse&gt; response) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread to optionally filter resource response content.</p>\n<p align=\"left\">&nbsp; // |request| and |response| represent the request and response respectively</p>\n<p align=\"left\">&nbsp; // and cannot be modified in this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual CefRefPtr&lt;CefResponseFilter&gt; GetResourceResponseFilter(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefResponse&gt; response) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return NULL;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread when a resource load has completed. |request| and</p>\n<p align=\"left\">&nbsp; // |response| represent the request and response respectively and cannot be</p>\n<p align=\"left\">&nbsp; // modified in this callback. |status| indicates the load completion status.</p>\n<p align=\"left\">&nbsp; // |received_content_length| is the number of response bytes actually read.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p>（6）OnResourceLoadComplete资源加载结束时的回调</p>\n<p align=\"left\">&nbsp; virtual void OnResourceLoadComplete(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;CefRefPtr&lt;CefResponse&gt; response,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; URLRequestStatus status,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int64 received_content_length) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread when the browser needs credentials from the user.</p>\n<p align=\"left\">&nbsp; // |isProxy| indicates whether the host is a proxy server. |host| contains the</p>\n<p align=\"left\">&nbsp; // hostname and |port| contains the port number. |realm| is the realm of the</p>\n<p align=\"left\">&nbsp; // challenge and may be empty. |scheme| is the authentication scheme used,</p>\n<p align=\"left\">&nbsp; // such as \"basic\" or \"digest\", and will be empty if the source of the request</p>\n<p align=\"left\">&nbsp; // is an FTP server. Return true to continue the request and call</p>\n<p align=\"left\">&nbsp; // CefAuthCallback::Continue() either in this method or at a later time when</p>\n<p align=\"left\">&nbsp; // the authentication information is available. Return false to cancel the</p>\n<p align=\"left\">&nbsp; // request immediately.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=realm,optional_param=scheme)--*/</p>\n<p align=\"left\">&nbsp; virtual bool GetAuthCredentials(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool isProxy,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; host,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int port,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; realm,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; scheme,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefAuthCallback&gt; callback) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread before sending a network request with a \"Cookie\"</p>\n<p align=\"left\">&nbsp; // request header. Return true to allow cookies to be included in the network</p>\n<p align=\"left\">&nbsp; // request or false to block cookies. The |request| object should not be</p>\n<p align=\"left\">&nbsp; // modified in this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool CanGetCookies(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return true;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread when receiving a network request with a</p>\n<p align=\"left\">&nbsp; // \"Set-Cookie\" response header value represented by |cookie|. Return true to</p>\n<p align=\"left\">&nbsp; // allow the cookie to be stored or false to block the cookie. The |request|</p>\n<p align=\"left\">&nbsp; // object should not be modified in this callback.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool CanSetCookie(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequest&gt; request,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefCookie&amp; cookie) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return true;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the IO thread when JavaScript requests a specific storage quota</p>\n<p align=\"left\">&nbsp; // size via the webkitStorageInfo.requestQuota function. |origin_url| is the</p>\n<p align=\"left\">&nbsp; // origin of the page making the request. |new_size| is the requested quota</p>\n<p align=\"left\">&nbsp; // size in bytes. Return true to continue the request and call</p>\n<p align=\"left\">&nbsp; // CefRequestCallback::Continue() either in this method or at a later time to</p>\n<p align=\"left\">&nbsp; // grant or deny the request. Return false to cancel the request immediately.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnQuotaRequest(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; origin_url,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;int64 new_size,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequestCallback&gt; callback) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the UI thread to handle requests for URLs with an unknown</p>\n<p align=\"left\">&nbsp; // protocol component. Set |allow_os_execution| to true to attempt execution</p>\n<p align=\"left\">&nbsp; // via the registered OS protocol handler, if any.</p>\n<p align=\"left\">&nbsp; // SECURITY WARNING: YOU SHOULD USE THIS METHOD TO ENFORCE RESTRICTIONS BASED</p>\n<p align=\"left\">&nbsp; // ON SCHEME, HOST OR OTHER URL ANALYSIS BEFORE ALLOWING OS EXECUTION.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp;&nbsp;virtual void OnProtocolExecution(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; url,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool&amp; allow_os_execution) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the UI thread to handle requests for URLs with an invalid</p>\n<p align=\"left\">&nbsp; // SSL certificate. Return true and call CefRequestCallback::Continue() either</p>\n<p align=\"left\">&nbsp; // in this method or at a later time to continue or cancel the request. Return</p>\n<p align=\"left\">&nbsp; // false to cancel the request immediately. If</p>\n<p align=\"left\">&nbsp; // CefSettings.ignore_certificate_errors is set all invalid certificates will</p>\n<p align=\"left\">&nbsp; // be accepted without calling this method.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnCertificateError(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cef_errorcode_t cert_error,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;const CefString&amp; request_url,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefSSLInfo&gt; ssl_info,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefRequestCallback&gt; callback) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the UI thread when a client certificate is being requested for</p>\n<p align=\"left\">&nbsp; // authentication. Return false to use the default behavior and automatically</p>\n<p align=\"left\">&nbsp; // select the first certificate available. Return true and call</p>\n<p align=\"left\">&nbsp; // CefSelectClientCertificateCallback::Select either in this method or at a</p>\n<p align=\"left\">&nbsp; // later time to select a certificate. Do not call Select or call it with NULL</p>\n<p align=\"left\">&nbsp; // to continue without using any certificate. |isProxy| indicates whether the</p>\n<p align=\"left\">&nbsp; // host is an HTTPS proxy or the origin server. |host| and |port| contains the</p>\n<p align=\"left\">&nbsp; // hostname and port of the SSL server. |certificates| is the list of</p>\n<p align=\"left\">&nbsp; // certificates to choose from; this list has already been pruned by Chromium</p>\n<p align=\"left\">&nbsp; // so that it only contains certificates from issuers that the server trusts.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual bool OnSelectClientCertificate(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; bool isProxy,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefString&amp; host,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int port,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const X509CertificateList&amp; certificates,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefSelectClientCertificateCallback&gt; callback) {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; return false;</p>\n<p align=\"left\">&nbsp; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the browser process UI thread when a plugin has crashed.</p>\n<p align=\"left\">&nbsp; // |plugin_path| is the path of the plugin that crashed.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnPluginCrashed(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;const CefString&amp; plugin_path) {}</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Called on the browser process UI thread when the render view associated</p>\n<p align=\"left\">&nbsp; // with |browser| is ready to receive/handle IPC messages in the render</p>\n<p align=\"left\">&nbsp; // process.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnRenderViewReady(CefRefPtr&lt;CefBrowser&gt; browser) {}</p>\n<p align=\"left\">///</p>\n<p align=\"left\">&nbsp; // Called on the browser process UI thread when the render process</p>\n<p align=\"left\">&nbsp; // terminates unexpectedly. |status| indicates how the process</p>\n<p align=\"left\">&nbsp; // terminated.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef()--*/</p>\n<p align=\"left\">&nbsp; virtual void OnRenderProcessTerminated(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TerminationStatus status) {}</p>\n<p align=\"left\">};</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>\n<h1>3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CEF高级应用<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h1>\n<h2>3.1&nbsp; CEF和JavaScript交互<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p><a href=\"https://bitbucket.org/chromiumembedded/cef/wiki/JavaScriptIntegration.md\" rel=\"noopener\">https://bitbucket.org/chromiumembedded/cef/wiki/JavaScriptIntegration.md</a></p>\n<p>CEF使用的V8 JavaScript 引擎用于内部JavaScript实现，每一个frame都有JS上下文（context），为JS代码执行提供范围和安全。CEF暴露了很多JS特性可以和客户端程序进行交互。</p>\n<h3>3.1.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 在CEF执行JavaScript脚本<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>应用场景是需要在CEF中拦截一个URL请求，并把它重定向到另外一个URL，可以调用pFrame-&gt;ExecuteJavaScript来执行一个JavaScript脚本，实现跳转。当然也可以实现其他应用功能。</p>\n<p align=\"left\">CefRefPtr&lt;CefFrame&gt; pFrame = browser-&gt;GetMainFrame();</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>std</em>::<em>string</em>&nbsp;strurl = pFrame-&gt;GetURL().ToString();</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<em>std</em>::<em>string</em>&nbsp;strname = pFrame-&gt;GetName().ToString();</p>\n<p align=\"left\">pFrame-&gt;GetName().ToString().c_str());</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (pFrame-&gt;GetURL() == \"https://10.19.141.75/portal/\")</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; pFrame-&gt;ExecuteJavaScript(\"var param= { url:\'https://10.19.141.75/ishelf-web/personalCenter\' }; \\</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; window.goToApp(param);\\</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; var paramEx = { isExtend:true };\\</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; window.extendScreen(paramEx);\\</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \", pFrame-&gt;GetURL(), 0);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</p>\n<h3>3.1.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 窗口绑定方式实现CEF设置JavaScript的变量<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>在CEF程序中，创建一个CefV8Value对象，获取上下文的窗口对象，注入窗口对象一个变量值，网页中就可以使用JavaScript获取这个变量值。窗口绑定在CefRenderProcessHandler::OnContextCreated()函数中。是上下文创建响应函数，窗口绑定方式在每次frame重新加载（context创建）时都会加载一遍，CEF程序可以在OnContextCreated()给每一个frame设置不同的变量值。</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Context&gt; context) {</p>\n<p>&nbsp; // Retrieve the context\'s window object.</p>\n<p>&nbsp; CefRefPtr&lt;CefV8Value&gt; object = context-&gt;GetGlobal();</p>\n<p>&nbsp;</p>\n<p>&nbsp; // Create a new V8 string value. See the \"Basic JS Types\" section below.</p>\n<p>&nbsp; CefRefPtr&lt;CefV8Value&gt; str = CefV8Value::CreateString(\"My Value!\");</p>\n<p>&nbsp;</p>\n<p>&nbsp; // Add the string to the window object as \"window.myval\". See the \"JS Objects\" section below.</p>\n<p>&nbsp; object-&gt;SetValue(\"myval\", str, V8_PROPERTY_ATTRIBUTE_NONE);</p>\n<p>}</p>\n<p>JavaScript in the frame can then interact with the window bindings.</p>\n<p>&nbsp;</p>\n<p>&lt;script language=\"JavaScript\"&gt;</p>\n<p>alert(window.myval); // Shows an alert box with \"My Value!\"</p>\n<p>&lt;/script&gt;</p>\n<h3>3.1.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 扩展方式（Extension）实现CEF设置JavaScript的变量<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>Extension方式和窗口绑定方式类似，但是Extension方式是为每一个frame加载到上下文context，一旦加载变不能在修改，没有加载之前，DOM是不存在的，尝试范围这个值的DOM会出现崩溃。Extension方式是在CefRenderProcessHandler::OnWebKitInitialized()函数中用CefRegisterExtension() 函数注册的，是在初始化函数中实现的，所以对于每一个frame都是一样的。</p>\n<p>void MyRenderProcessHandler::OnWebKitInitialized() {</p>\n<p>&nbsp; // Define the extension contents.</p>\n<p>&nbsp; std::string extensionCode =</p>\n<p>&nbsp;&nbsp;&nbsp; \"var test;\"</p>\n<p>&nbsp;&nbsp;&nbsp; \"if (!test)\"</p>\n<p>&nbsp;&nbsp;&nbsp; \"&nbsp; test = {};\"</p>\n<p>&nbsp;&nbsp;&nbsp; \"(function() {\"</p>\n<p>&nbsp;&nbsp;&nbsp; \"&nbsp; test.myval = \'My Value!\';\"</p>\n<p>&nbsp;&nbsp;&nbsp; \"})();\";</p>\n<p>&nbsp;</p>\n<p>&nbsp; // Register the extension.</p>\n<p>&nbsp; CefRegisterExtension(\"v8/test\", extensionCode, NULL);</p>\n<p>}</p>\n<p>JS中调用变量值</p>\n<p>&lt;script language=\"JavaScript\"&gt;</p>\n<p>alert(test.myval); // Shows an alert box with \"My Value!\"</p>\n<p>&lt;/script&gt;</p>\n<h3>3.1.4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 窗口绑定方式实现CEF给JavaScript提供函数<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>（1）&nbsp;&nbsp; 自定义类实现CefV8Handler类，实现Execute接口，JavaScript执行函数后，会将函数名称、参数和返回值引用传递给Execute函数，Execute函数根据函数名去调用函数，函数的具体实现在Execute中，然后执行返回返回值。</p>\n<p>class MyV8Handler : public CefV8Handler {</p>\n<p>public:</p>\n<p>&nbsp; MyV8Handler() {}</p>\n<p>&nbsp;</p>\n<p>&nbsp; virtual bool Execute(const CefString&amp; name,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefV8ValueList&amp; arguments,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Value&gt;&amp; retval,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) OVERRIDE {</p>\n<p>&nbsp;&nbsp;&nbsp; if (name == \"myfunc\") {</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Return my string value.</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>retval&nbsp;</strong>= CefV8Value::CreateString(\"My Value!\");</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;</p>\n<p>&nbsp;&nbsp;&nbsp; }</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp; // Function does not exist.</p>\n<p>&nbsp;&nbsp;&nbsp; return false;</p>\n<p>&nbsp; }</p>\n<p>&nbsp;</p>\n<p>&nbsp; // Provide the reference counting implementation for this class.</p>\n<p>&nbsp; IMPLEMENT_REFCOUNTING(MyV8Handler);</p>\n<p>};</p>\n<p>（2）将函数名称设置到窗口对象，提供接受调用的handle</p>\n<p>void MyRenderProcessHandler::OnContextCreated(</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame,</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Context&gt; context) {</p>\n<p>&nbsp; // Retrieve the context\'s window object.</p>\n<p>&nbsp; CefRefPtr&lt;CefV8Value&gt; object = context-&gt;GetGlobal();</p>\n<p>&nbsp;</p>\n<p>&nbsp; // Create an instance of my CefV8Handler object.</p>\n<p>&nbsp; CefRefPtr&lt;CefV8Handler&gt; handler = new MyV8Handler();</p>\n<p>&nbsp;</p>\n<p>&nbsp; // Create the \"myfunc\" function.</p>\n<p>&nbsp; CefRefPtr&lt;CefV8Value&gt; func = CefV8Value::CreateFunction(\"myfunc\", handler);</p>\n<p>&nbsp;</p>\n<p>&nbsp; // Add the \"myfunc\" function to the \"window\" object.</p>\n<p>&nbsp; object-&gt;SetValue(\"myfunc\", func, V8_PROPERTY_ATTRIBUTE_NONE);</p>\n<p>}</p>\n<p>（3）JavaScript执行函数调用，就会进入Execute函数，返回返回值，alert会以弹窗形式展示结果。</p>\n<pre>&lt;script language=\"JavaScript\"&gt;</pre>\n<pre>alert(window.myfunc()); // Shows an alert box with \"My Value!\"</pre>\n<pre>&lt;/script&gt;</pre>\n<p>&nbsp;</p>\n<h3>3.1.5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Extension方式实现CEF给JavaScript提供函数<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>JavaScript调用CEF中的函数步骤：</p>\n<p>（1）实现app类，继承与CefApp，重写OnWebKitInitialized，在OnWebKitInitialized函数内部使用字符串定义函数。CEF调用CefRegisterExtension函数向JavaScript注册函数，并提供处理调用的handler。</p>\n<p align=\"left\">//CefClientApp.h</p>\n<p align=\"left\">class CCefClientApp : public CefApp, public CefBrowserProcessHandler, CefRenderProcessHandler</p>\n<p align=\"left\">{</p>\n<p align=\"left\">public:</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; CCefClientApp();</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; ~CCefClientApp();</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; //所有的CEF接口 都需要重载GetXXXHandler，并且return this，才会有效</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; virtual CefRefPtr&lt;CefRenderProcessHandler&gt; GetRenderProcessHandler() override { return this; }</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; //===========CefRenderProcessHandler</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; virtual void OnWebKitInitialized() override;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">private:</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CCEFV8HandlerEx&gt; v8Handler_;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; // Include the default reference counting implementation.</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; IMPLEMENT_REFCOUNTING(CCefClientApp);</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">private:</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">};</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">//CefClientApp.cpp</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">void CCefClientApp::OnWebKitInitialized()</p>\n<p align=\"left\">{</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; //MessageBox(NULL,L\"OnWebKitInitialized\\n\",0,0);</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; std::string app_code =</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //-----------------------------------</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //声明JavaScript里要调用的Cpp方法</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"var app;\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"if (!app)\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp; app = {};\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"(function() {\"</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp; jsInvokeCPlusPlus 实例函数</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp; app.jsInvokeCPlusPlus = function(v1, v2) {\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp;&nbsp;&nbsp; native function jsInvokeCPlusPlus();\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;\"&nbsp;&nbsp;&nbsp; return jsInvokeCPlusPlus(v1, v2);\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp; };\"</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //函数</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp; app.jsTransform = function(v1) {\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp;&nbsp;&nbsp; native function jsTransform();\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp;&nbsp;&nbsp; return jsTransform(v1);\"</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"&nbsp; };\"</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \"})();\";</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; // Register app extension module</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; // JavaScript里调用app.jsInvokeCPlusPlus时，就会去通过CefRegisterExtension注册的CefV8Handler列表里查找</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; // 找到\"v8/app\"对应的CCEFV8HandlerEx，就调用它的Execute方法</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; // 假设v8Handler_是CCefClientApp的一个成员变量</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; //v8Handler_ = new CCEFV8HandlerEx();</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp; CefRegisterExtension(\"v8/app\", app_code, v8Handler_);</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">}</p>\n<p><strong>（2</strong><strong>）JavaScript</strong><strong>调用函数</strong></p>\n<p>&lt;html&gt;</p>\n<p>&lt;script&gt;</p>\n<p>app.<strong>jsInvokeCPlusPlus</strong>(\"123\",\"xyz\");</p>\n<p>app.<strong>jsTransform</strong>(\"hello world\");</p>\n<p>&lt;/script&gt;</p>\n<p>&lt;/html&gt;</p>\n<p><strong>（3</strong><strong>）&nbsp;&nbsp;&nbsp; handler</strong><strong>的Execute</strong><strong>函数接收调用响应，根据函数名称判断是调用哪个函数，获取参数调用函数。</strong></p>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">//CEFV8HandlerEx.h<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\"> \n \n \n CefV8Handler \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">public:<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">public:<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-delphi highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n \n \n CefString\n <span class=\"hljs-keyword\">name</span>\n CefRefPtr\nCefV8Value\n <span class=\"hljs-keyword\">object</span>\n \n CefV8ValueList\n arguments\n CefRefPtr\nCefV8Value\n retval\n CefString\n exception\n <span class=\"hljs-keyword\">override</span>\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">private:<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-avrasm highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n <span class=\"hljs-keyword\">std</span>\nmap\n<span class=\"hljs-keyword\">std</span>\npair\n<span class=\"hljs-keyword\">std</span>\nstring\n \n <span class=\"hljs-keyword\">std</span>\npair\nCefRefPtr\nCefV8Context\n CefRefPtr\nCefV8Value\n \n \nCallbackMap\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; CallbackMap callback_map_\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">public:<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \nCCEFV8HandlerEx\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">};<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">//CEFV8HandlerEx.cpp<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">//JS调用C++函数的回调<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-typescript highlighter-hljs\"> <span class=\"hljs-title class_\">CCEFV8HandlerEx</span>\n <span class=\"hljs-title class_\">CefString</span>\n name&nbsp; \n <span class=\"hljs-title class_\">CefRefPtr</span>\n<span class=\"hljs-title class_\">CefV8Value</span>\n <span class=\"hljs-built_in\">object</span> \n \n <span class=\"hljs-title class_\">CefV8ValueList</span>\n <span class=\"hljs-variable language_\">arguments</span> \n <span class=\"hljs-title class_\">CefRefPtr</span>\n<span class=\"hljs-title class_\">CefV8Value</span>\n retval \n <span class=\"hljs-title class_\">CefString</span>\n exception\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">{<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-delphi highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n \n<span class=\"hljs-keyword\">name</span> \n \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-javascript highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n \n<span class=\"hljs-variable language_\">arguments</span>\n \n \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-javascript highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"hljs-title class_\">CefString</span> strParam1 \n <span class=\"hljs-variable language_\">arguments</span>\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-javascript highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"hljs-title class_\">CefString</span> strParam2 \n <span class=\"hljs-variable language_\">arguments</span>\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TCHAR szBuffer\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \nszBuffer\n \nszBuffer\n \n strParam1\n strParam2\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n szBuffer\n \n MB_OK\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; retval \n CefV8Value\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; retval \n CefV8Value\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-delphi highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n \n<span class=\"hljs-keyword\">name</span> \n L\n \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-javascript highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <span class=\"hljs-title class_\">CefString</span> strParam1 \n <span class=\"hljs-variable language_\">arguments</span>\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TCHAR szBuffer\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \nszBuffer\n \nszBuffer\n \n strParam1\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; \n szBuffer\n \n MB_OK\n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\"><code class=\"hljs language-undefined highlighter-hljs\">&nbsp;&nbsp;&nbsp; \n \n\n</code></pre>\n<pre class=\"highlighter-hljs\" highlighted=\"true\">}<code class=\"hljs language-undefined highlighter-hljs\"></code></pre>\n<h2>3.2&nbsp; CefV8Value类实现定JavaScript数据类型<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<p>上面的实例中我们已经用到CefV8Value类的CreateString(\"My Value!\")创建字符串和CreateFunction创建了函数。这个类还可以创建, null, bool, int, double, date string等数据类型，都有对应的函数。static CefRefPtr&lt;CefV8Value&gt; CreateArray(int length)创建JavaScript数组。下面详细讲解下给JavaScript提供创建对象。accessor就是给JavaScript提供调用的set和get方法设置或获取属性。interceptor是拦截器，拦截set和get方法。</p>\n<p align=\"left\">///</p>\n<p align=\"left\">&nbsp; // Create a new CefV8Value object of type object with optional accessor and/or</p>\n<p align=\"left\">&nbsp; // interceptor. This method should only be called from within the scope of a</p>\n<p align=\"left\">&nbsp; // CefRenderProcessHandler, CefV8Handler or CefV8Accessor callback, or in</p>\n<p align=\"left\">&nbsp; // combination with calling Enter() and Exit() on a stored CefV8Context</p>\n<p align=\"left\">&nbsp; // reference.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(optional_param=accessor, optional_param=interceptor)--*/</p>\n<p align=\"left\">&nbsp; static CefRefPtr&lt;CefV8Value&gt; CreateObject(</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Accessor&gt; accessor,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Interceptor&gt; interceptor);</p>\n<h3>3.2.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 一般对象<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>如果不需要给JavaScript提供get和set方法，可以直接创建对象。</p>\n<p>CefRefPtr&lt;CefV8Value&gt; obj = CefV8Value::CreateObject(<strong>NULL</strong>);</p>\n<p>给对象设置属性名称为myval，属性值为My String;字符串类型。</p>\n<p>virtual bool SetValue(int index, CefRefPtr&lt;CefV8Value&gt; value) = 0;</p>\n<p>obj-&gt;SetValue(\"myval\", CefV8Value::CreateString(\"My String!\"));</p>\n<h3>3.2.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CEF实现带access的JavaScript对象<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>如果要提供set和get方法，需要提供access实现类。</p>\n<p><strong>（1</strong><strong>）实现access</strong><strong>类</strong></p>\n<pre><strong>class</strong> MyV8Accessor : <strong>public</strong> CefV8Accessor {</pre>\n<pre><strong>public</strong><strong>:</strong></pre>\n<pre>&nbsp; MyV8Accessor() {}</pre>\n<pre>&nbsp;</pre>\n<pre>&nbsp; virtual <strong>bool</strong> Get(<strong>const</strong> CefString<strong>&amp;</strong> name,</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>const</strong> CefRefPtr<strong>&lt;</strong>CefV8Value<strong>&gt;</strong> object,</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr<strong>&lt;</strong>CefV8Value<strong>&gt;&amp;</strong> retval,</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString<strong>&amp;</strong> exception) OVERRIDE {</pre>\n<pre>&nbsp;&nbsp;&nbsp; <strong>if</strong> (name <strong>==</strong> \"myval\") {</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>// Return the value.</em></pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; retval <strong>=</strong> CefV8Value<strong>::</strong>CreateString(myval_);</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>return</strong> <strong>true</strong>;</pre>\n<pre>&nbsp; &nbsp;&nbsp;}</pre>\n<pre>&nbsp;</pre>\n<pre>&nbsp;&nbsp;&nbsp; <em>// Value does not exist.</em></pre>\n<pre>&nbsp;&nbsp;&nbsp; <strong>return</strong> <strong>false</strong>;</pre>\n<pre>&nbsp; }</pre>\n<pre>&nbsp;</pre>\n<pre>&nbsp; virtual <strong>bool</strong> Set(<strong>const</strong> CefString<strong>&amp;</strong> name,</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>const</strong> CefRefPtr<strong>&lt;</strong>CefV8Value<strong>&gt;</strong> object,</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>const</strong> CefRefPtr<strong>&lt;</strong>CefV8Value<strong>&gt;</strong> value,</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString<strong>&amp;</strong> exception) OVERRIDE {</pre>\n<pre>&nbsp;&nbsp;&nbsp; <strong>if</strong> (name <strong>==</strong> \"myval\") {</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>if</strong> (value<strong>-&gt;</strong>IsString()) {</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>// Store the value.</em></pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; myval_ <strong>=</strong> value<strong>-&gt;</strong>GetStringValue();</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; } <strong>else</strong> {</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>// Throw an exception.</em></pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; exception <strong>=</strong> \"Invalid value type\";</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }</pre>\n<pre>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>return</strong> <strong>true</strong>;</pre>\n<pre>&nbsp; &nbsp;&nbsp;}</pre>\n<pre>&nbsp;</pre>\n<pre>&nbsp;&nbsp;&nbsp; <em>// Value does not exist.</em></pre>\n<pre>&nbsp;&nbsp;&nbsp; <strong>return</strong> <strong>false</strong>;</pre>\n<pre>&nbsp; }</pre>\n<pre>&nbsp;</pre>\n<pre>&nbsp; <em>// Variable used for storing the value.</em></pre>\n<pre>&nbsp; CefString myval_;</pre>\n<pre>&nbsp;</pre>\n<pre>&nbsp; <em>// Provide the reference counting implementation for this class.</em></pre>\n<pre>&nbsp; IMPLEMENT_REFCOUNTING(MyV8Accessor);</pre>\n<pre>};</pre>\n<p align=\"left\"><strong>（2</strong><strong>）使用SetValue</strong><strong>函数设置对象属性采用access</strong><strong>方式设置和获取数据。把对象设置到窗口对象中。</strong></p>\n<p align=\"left\">virtual bool SetValue(const CefString&amp; key,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;AccessControl settings,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; PropertyAttribute attribute) = 0;</p>\n<p align=\"left\">typedef enum {</p>\n<p align=\"left\">&nbsp; V8_ACCESS_CONTROL_DEFAULT = 0,</p>\n<p align=\"left\">&nbsp; V8_ACCESS_CONTROL_ALL_CAN_READ = 1,</p>\n<p align=\"left\">&nbsp; V8_ACCESS_CONTROL_ALL_CAN_WRITE = 1 &lt;&lt; 1,</p>\n<p align=\"left\">&nbsp; V8_ACCESS_CONTROL_PROHIBITS_OVERWRITING = 1 &lt;&lt; 2</p>\n<p align=\"left\">} cef_v8_accesscontrol_t;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">///</p>\n<p align=\"left\">// V8 property attribute values.</p>\n<p align=\"left\">///</p>\n<p align=\"left\">typedef enum {</p>\n<p align=\"left\">&nbsp; V8_PROPERTY_ATTRIBUTE_NONE = 0,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Writeable, Enumerable,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;&nbsp; Configurable</p>\n<p align=\"left\">&nbsp; V8_PROPERTY_ATTRIBUTE_READONLY = 1 &lt;&lt; 0,&nbsp;&nbsp; // Not writeable</p>\n<p align=\"left\">&nbsp; V8_PROPERTY_ATTRIBUTE_DONTENUM = 1 &lt;&lt; 1,&nbsp;&nbsp; // Not enumerable</p>\n<p align=\"left\">&nbsp; V8_PROPERTY_ATTRIBUTE_DONTDELETE = 1 &lt;&lt; 2&nbsp; // Not configurable</p>\n<p>} cef_v8_propertyattribute_t;</p>\n<p>&nbsp;</p>\n<p>obj-&gt;SetValue(\"myval\", V8_ACCESS_CONTROL_DEFAULT,</p>\n<p>V8_PROPERTY_ATTRIBUTE_NONE);</p>\n<p>（3）JavaScript中调用set和get方法，就可以设置和获取属性值。</p>\n<h3>3.2.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CEF实现带拦截器CefV8Interceptor的JavaScript对象<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>拦截器CefV8Interceptor和Access的区别是，除了可以用字符串来映射属性，还可以用index索引来映射属性。其定义如下</p>\n<p align=\"left\">// Interface that should be implemented to handle V8 interceptor calls. The</p>\n<p align=\"left\">// methods of this class will be called on the thread associated with the V8</p>\n<p align=\"left\">// interceptor. Interceptor\'s named property handlers (with first argument of</p>\n<p align=\"left\">// type CefString) are called when object is indexed by string. Indexed property</p>\n<p align=\"left\">// handlers (with first argument of type int) are called when object is indexed</p>\n<p align=\"left\">// by integer.</p>\n<p align=\"left\">///</p>\n<p align=\"left\">/*--cef(source=client,no_debugct_check)--*/</p>\n<p align=\"left\">class CefV8Interceptor : public virtual CefBaseRefCounted {</p>\n<p align=\"left\">&nbsp;public:</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Handle retrieval of the interceptor value identified by |name|. |object| is</p>\n<p align=\"left\">&nbsp; // the receiver (\'this\' object) of the interceptor. If retrieval succeeds, set</p>\n<p align=\"left\">&nbsp; // |retval| to the return value. If the requested value does not exist, don\'t</p>\n<p align=\"left\">&nbsp; // set either |retval| or |exception|. If retrieval fails, set |exception| to</p>\n<p align=\"left\">&nbsp; // the exception that will be thrown. If the property has an associated</p>\n<p align=\"left\">&nbsp; // accessor, it will be called only if you don\'t set |retval|.</p>\n<p align=\"left\">&nbsp; // Return true if interceptor retrieval was handled, false otherwise.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(capi_name=get_byname)--*/</p>\n<p align=\"left\">&nbsp; virtual bool Get(const CefString&amp; name,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Value&gt;&amp; retval,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Handle retrieval of the interceptor value identified by |index|. |object|</p>\n<p align=\"left\">&nbsp; // is the receiver (\'this\' object) of the interceptor. If retrieval succeeds,</p>\n<p align=\"left\">&nbsp; // set |retval| to the return value. If the requested value does not exist,</p>\n<p align=\"left\">&nbsp; // don\'t set either |retval| or |exception|. If retrieval fails, set</p>\n<p align=\"left\">&nbsp; // |exception| to the exception that will be thrown.</p>\n<p align=\"left\">&nbsp; // Return true if interceptor retrieval was handled, false otherwise.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(capi_name=get_byindex,index_param=index)--*/</p>\n<p align=\"left\">&nbsp; virtual bool Get(int index,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Value&gt;&amp; retval,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Handle assignment of the interceptor value identified by |name|. |object|</p>\n<p align=\"left\">&nbsp; // is the receiver (\'this\' object) of the interceptor. |value| is the new</p>\n<p align=\"left\">&nbsp; // value being assigned to the interceptor. If assignment fails, set</p>\n<p align=\"left\">&nbsp; // |exception| to the exception that will be thrown. This setter will always</p>\n<p align=\"left\">&nbsp; // be called, even when the property has an associated accessor.</p>\n<p align=\"left\">&nbsp; // Return true if interceptor assignment was handled, false otherwise.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(capi_name=set_byname)--*/</p>\n<p align=\"left\">&nbsp; virtual bool Set(const CefString&amp; name,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; value,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) = 0;</p>\n<p align=\"left\">&nbsp;</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Handle assignment of the interceptor value identified by |index|. |object|</p>\n<p align=\"left\">&nbsp; // is the receiver (\'this\' object) of the interceptor. |value| is the new</p>\n<p align=\"left\">&nbsp; // value being assigned to the interceptor. If assignment fails, set</p>\n<p align=\"left\">&nbsp; // |exception| to the exception that will be thrown.</p>\n<p align=\"left\">&nbsp; // Return true if interceptor assignment was handled, false otherwise.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; /*--cef(capi_name=set_byindex,index_param=index)--*/</p>\n<p align=\"left\">&nbsp; virtual bool Set(int index,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; value,</p>\n<p align=\"left\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) = 0;</p>\n<p align=\"left\">};</p>\n<p>实例步骤如下</p>\n<p><strong>（1</strong><strong>）实现Interceptor</strong><strong>类</strong></p>\n<p>class Interceptor : public CefV8Interceptor {</p>\n<p>&nbsp;&nbsp; public:</p>\n<p>&nbsp;&nbsp;&nbsp; Interceptor() {}</p>\n<p>&nbsp;&nbsp;&nbsp; virtual bool Get(const CefString&amp; name,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Value&gt;&amp; retval,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) OVERRIDE {</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;</p>\n<p>&nbsp;&nbsp;&nbsp; }</p>\n<p>&nbsp;&nbsp;&nbsp; virtual bool Get(int index,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefV8Value&gt;&amp; retval,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) OVERRIDE {</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;</p>\n<p>&nbsp;&nbsp;&nbsp; }</p>\n<p>&nbsp;&nbsp;&nbsp; virtual bool Set(const CefString&amp; name,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; value,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) OVERRIDE {</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;</p>\n<p>&nbsp;&nbsp;&nbsp; }</p>\n<p>&nbsp;&nbsp;&nbsp; virtual bool Set(int index,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; object,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; const CefRefPtr&lt;CefV8Value&gt; value,</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString&amp; exception) OVERRIDE {</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;</p>\n<p>&nbsp;&nbsp;&nbsp; }</p>\n<p>&nbsp;&nbsp;&nbsp; IMPLEMENT_REFCOUNTING(Interceptor);</p>\n<p>&nbsp; };</p>\n<p><strong>（2</strong><strong>）创建Interceptor</strong><strong>对象和JavaScript</strong><strong>对象</strong></p>\n<p>&nbsp; CefRefPtr&lt;CefV8Interceptor&gt; interceptor = new Interceptor();</p>\n<p>&nbsp; PERF_ITERATIONS_START()</p>\n<p>&nbsp; CefRefPtr&lt;CefV8Value&gt; value = CefV8Value::CreateObject(nullptr, interceptor);</p>\n<p>（3）SetValue（）函设置到窗口对象中（待验证）</p>\n<p>（4）JavaScript调用按照字符串名称或者索引来设置获取值。</p>\n<h2>3.3&nbsp; CEF进程间通讯<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h2>\n<h3>3.3.1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 进程间通讯函数<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>CEF有Browser进程和Renderer进程，进程之间可以相互通信。</p>\n<p>发送消息Browser进程和Renderer进程都是一样的，使用CefBrowser::SendProcessMessage() ，SendProcessMessage第一个参数是CefProcessId，是一个枚举类型，给Browser进程发送，就用PID_BROWSER，给Render进程发送，就用PID_RENDERER。</p>\n<p align=\"left\">typedef enum {</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Browser process.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; PID_BROWSER,</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; // Renderer process.</p>\n<p align=\"left\">&nbsp; ///</p>\n<p align=\"left\">&nbsp; PID_RENDERER,</p>\n<p>} cef_process_id_t;</p>\n<p>Render进程这边， 重写CefRenderProcessHandler::OnProcessMessageReceived()</p>\n<p>Browser进程这边，重写CefClient::OnProcessMessageReceived()这个方法来处理跨进程消息。</p>\n<h3>3.3.2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 进程通讯实例<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p><strong>（1</strong><strong>）renderer</strong><strong>进程发送消息</strong></p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefProcessMessage&gt; msg = CefProcessMessage::Create(\"login_msg\");</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Retrieve the argument list object.</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefListValue&gt; args = msg-&gt;GetArgumentList();</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Populate the argument values.</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; args-&gt;SetSize(2);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; args-&gt;SetString(0, strUser);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; args-&gt;SetString(1, strPassword);</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Send the process message to the browser process.</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefV8Context::GetCurrentContext()-&gt;GetBrowser()-&gt;SendProcessMessage(PID_BROWSER, msg);</p>\n<p><strong>（2</strong><strong>）Browser</strong><strong>进程这边，重写CefClient::OnProcessMessageReceived()</strong><strong>这个方法来处理跨进程消息。接收到消息之后再回复消息。</strong></p>\n<p>bool ClientHandler::<strong>OnProcessMessageReceived</strong>(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p>&nbsp;&nbsp;&nbsp; CefProcessId source_process,</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefProcessMessage&gt; message)</p>\n<p>{</p>\n<p>&nbsp;&nbsp;&nbsp; const std::string&amp; messageName = message-&gt;GetName();</p>\n<p>&nbsp;&nbsp;&nbsp; if (messageName == \"login_msg\")</p>\n<p>&nbsp;&nbsp;&nbsp; {&nbsp;&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // extract message</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefListValue&gt; args = message-&gt;GetArgumentList();</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString strUser = args-&gt;GetString(0);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefString strPassword = args-&gt;GetString(1);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; TCHAR szLog[256] = { 0 };</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; _stprintf_s(szLog, 256, _T(\"BrowserProcess, user - %s, password - %s\\r\\n\"), strUser.c_str(), strPassword.c_str());</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OutputDebugString(szLog);</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //send reply to render process</p>\n<p>&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;CefRefPtr&lt;CefProcessMessage&gt; outMsg = CefProcessMessage::Create(\"login_reply\");</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Retrieve the argument list object.</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefListValue&gt; replyArgs = outMsg-&gt;GetArgumentList();</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Populate the argument values.</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; replyArgs-&gt;SetSize(1);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; replyArgs-&gt;SetInt(0, 0);</p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // Send the process message to the renderer process.</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<strong>&nbsp;browser-&gt;SendProcessMessage(PID_RENDERER, outMsg);</strong></p>\n<p>&nbsp;</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;</p>\n<p>&nbsp;&nbsp;&nbsp; }</p>\n<p>&nbsp;&nbsp;&nbsp; return false;</p>\n<p>}</p>\n<p align=\"left\"><strong>（3</strong><strong>）Render</strong><strong>进程这边，&nbsp;</strong><strong>重写CefRenderProcessHandler:: OnProcessMessageReceived()</strong><strong>方法来处理来自Browser</strong><strong>进程的消息。</strong></p>\n<p>bool ClientAppRenderer::OnProcessMessageReceived(CefRefPtr&lt;CefBrowser&gt; browser,</p>\n<p>&nbsp;&nbsp;&nbsp; CefProcessId source_process,</p>\n<p>&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefProcessMessage&gt; message)</p>\n<p>{</p>\n<p>&nbsp;&nbsp;&nbsp; const std::string&amp; messageName = message-&gt;GetName();</p>\n<p>&nbsp;&nbsp;&nbsp; if (messageName == \"login_reply\")</p>\n<p>&nbsp;&nbsp;&nbsp; {</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; // extract message</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefListValue&gt; args = message-&gt;GetArgumentList();</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; int status = args-&gt;GetInt(0);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; OutputDebugString(status == 0 ? _T(\"Renderer process, login ok\\r\\n\") : _T(\"Renderer process, login failed\\r\\n\"));</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CefRefPtr&lt;CefFrame&gt; frame = browser-&gt;GetMainFrame();</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; frame-&gt;ExecuteJavaScript(\"alert(\'Got Login Reply from Browser process\')\", frame-&gt;GetURL(), 0);</p>\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return true;</p>\n<p>&nbsp;&nbsp;&nbsp; }</p>\n<p>&nbsp;&nbsp;&nbsp; return false;</p>\n<p>}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<h3>3.3.3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; CEF指定frame通讯<button class=\"cnblogs-toc-button\" title=\"显示目录导航\" aria-expanded=\"false\"></button></h3>\n<p>一个网页有多个frame，有一个mainframe和多个subframe，发送消息时，将frame的id放入参数中一起发过去，接收时消息时，获取frameid，就可以实现指定frame通讯。因为frameid是一个int64类型的数据，所以发送时将它分解为两个int32</p>\n<p>的高低位数据，接收数据时，再将两个int32的数据拼接成一个int64数据。</p>\n<p>#define MAKE_INT64(int_low, int_high) \\</p>\n<p>&nbsp;&nbsp;&nbsp; ((int64) (((int) (int_low)) | ((int64) ((int) (int_high))) &lt;&lt; 32))</p>\n<p>#define LOW_INT(int64_val) ((int) (int64_val))</p>\n<p>#define HIGH_INT(int64_val) ((int) (((int64) (int64_val) &gt;&gt; 32) &amp; 0xFFFFFFFFL))</p>\n<p>&nbsp;</p>\n<p>// Sending the frame ID.</p>\n<p>const int64 frame_id = frame-&gt;GetIdentifier();</p>\n<p>args-&gt;SetInt(0, LOW_INT(frame_id));</p>\n<p>args-&gt;SetInt(1, HIGH_INT(frame_id));</p>\n<p>&nbsp;</p>\n<p>// Receiving the frame ID.</p>\n<p>const int64 frame_id = MAKE_INT64(args-&gt;GetInt(0), args-&gt;GetInt(1));</p>\n<p>CefRefPtr&lt;CefFrame&gt; frame = browser-&gt;GetFrame(frame_id);</p>\n<div id=\"MySignature\">自己开发了一个股票智能分析软件，功能很强大，需要的点击下面的链接获取： https://www.cnblogs.com/bclshuai/p/11380657.html</div>\n</div>\n', 'Html测试', '', '', 0, NULL, NULL, NULL, 15, 1, '433082606611861504', '2023-04-18 03:34:54.002', '433082606611861504', '2023-05-02 13:19:36.025');
